from typing import Dict, Tuple, List, Set

def find_vocabulary_size(current_vocab: Dict[str, int]) -> int:
  vocab = set()

  for word in current_vocab.keys():
    # Separamos la palabra en subpalabras si es necesario y las a√±adimos al conjunto
    for subword in word.split():
      vocab.add(subword)

  # Retornamos el tama√±o del conjunto, que es el n√∫mero de palabras √∫nicas
  return len(vocab)

